import React, { useEffect, useState } from "react";
import LlmSettings from "./components/LlmSettings";
import PromptPanel from "./components/PromptPanel";
import DiagramCanvas from "./components/DiagramCanvas";
import LandingPage from "./components/LandingPage";

export default function App() {
  const [theme, setTheme] = useState<"light" | "dark">(
    () => (localStorage.getItem("theme") as any) || "dark"
  );

  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [showApp, setShowApp] = useState(() => window.location.hash === "#app");

  useEffect(() => {
    const handleHashChange = () => {
      setShowApp(window.location.hash === "#app");
    };
    window.addEventListener("hashchange", handleHashChange);
    return () => window.removeEventListener("hashchange", handleHashChange);
  }, []);

  useEffect(() => {
    const root = document.documentElement;
    root.setAttribute("data-theme", theme);
    localStorage.setItem("theme", theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prev) => prev === "light" ? "dark" : "light");
  };

  const getThemeIcon = () => {
    return theme === "light" ? "‚òÄÔ∏è" : "üåô";
  };

  const enterApp = () => {
    window.location.hash = "#app";
    setShowApp(true);
  };

  if (!showApp) {
    return <LandingPage onEnterApp={enterApp} />;
  }

  return (
    <div className={`app ${sidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
      <div className={`sidebar-overlay ${sidebarOpen ? 'open' : ''}`} onClick={() => setSidebarOpen(false)} />
      <button className="mobile-toggle" onClick={() => setSidebarOpen(!sidebarOpen)} title="Ayarlarƒ± A√ß/Kapat">
        {sidebarOpen ? "‚úï" : "‚öôÔ∏è"}
      </button>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h1>{sidebarCollapsed ? "‚ú®" : "‚ú® Diagram AI"}</h1>
          <div style={{ display: 'flex', gap: '4px' }}>
            <button className="theme-toggle collapse-toggle" onClick={() => setSidebarCollapsed(!sidebarCollapsed)} title={sidebarCollapsed ? "Geni≈ület" : "K√º√ß√ºlt"}>
              {sidebarCollapsed ? "¬ª" : "¬´"}
            </button>
            <button className="theme-toggle" onClick={toggleTheme} title="Tema Deƒüi≈ütir">
              {getThemeIcon()}
            </button>
          </div>
        </div>
        {!sidebarCollapsed && (
          <>
            <LlmSettings />
            <PromptPanel />
          </>
        )}
      </div>
      <div className="main">
        <DiagramCanvas />
      </div>
    </div>
  );
}
